/**
 * Given two strings s and t which consist of only lowercase letters.
 * String t is generated by random shuffling string s and then add one more letter at a random position.
 * Find the letter that was added in t.
 * Example:
 * Input:
 * s = "abcd"
 * t = "abcde
 * Output:
 * e
 * Explanation:
 * 'e' is the letter that was added.
 * 
 * Tags: HashTable, Bit Manipulation
 * @author wendi
 *
 */
public class FindtheDifference {
	
	/**
	 * Method2: Bit Manipulation: c = c ^ a ^ a.
	 * @param String s, String t
	 * @return char
	 * Time: O(n)
	 * Space: O(1)
	 */
	public char findtheDifferenceI(String s, String t) {
		char C = 0;
		for (char c: s.toCharArray()) {
			C ^= c;
		}
		for (char c: t.toCharArray()) {
			C ^= c;
		}
		return C;
	}	
	
	/**
	 * Method1: Array(instead of hashTable)
	 * @param char s, char t
	 * @return char
	 * Time: O(n)
	 * Space: O(1)
	 */
	public char findtheDifference(String s, String t) {
		int[] alph = new int[26];
		for (char c: s.toCharArray()) {
			alph[c - 'a']++;
		}
		for (char c: t.toCharArray()) {
			alph[c - 'a']--;
			if (alph[c - 'a'] < 0) return c;
		}
		return 0;
	}

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		FindtheDifference result = new FindtheDifference();
		System.out.println(result.findtheDifference("abcd", "ebacd"));
		String s = "";
		String t = "";
		System.out.println(result.findtheDifference(s, t));
		System.out.println(result.findtheDifferenceI(s, t));
	}

}
